import React, { useEffect, useState } from 'react'

const Interest = () => {
	const [getInterest, setInterest] = useState([]);
	
	const getInterests = () => {
		try {
			setInterest(
				[
					{
						interest: 'Pemrograman WEB',
						caption: 'Pemrograman WEB adalah ...',
						imageLink: './img/interests/html-css-js.svg',
					},
					{
						interest: 'Desain UI/UX',
						caption: 'Desain UI/UX adalah ...',
						imageLink: './img/interests/react.svg',
					},
					{
						interest: 'Desain Grafis',
						caption: 'Desain Grafis adalah ...',
						imageLink: './img/interests/Tailwind_CSS_logo.svg',
					},
					{
						interest: 'Penulis Konten',
						caption: 'Penulis Konten adalah ...',
						imageLink: './img/interests/Bootstrap_logo.svg',
					},
				]
			)
		} catch (error) {
			console.log(error.message);
		}
	}

	useEffect(() => {
		getInterests();
	}, [])

	return (
		<section id='Interest' className='mt-24'>
			<div className='p-5 text-center'>
				<h1 className="text-5xl font-bold">My Interest</h1>
				<p className="py-6">
				I love what I do. I take great pride in what I do.
				</p>
			</div>
			<div class="card min-w-screen m-5 bg-base-100 shadow-xl grid lg:grid-cols-3 gap-4">
				<div className='className="container mx-autosm:flex sm:flex-wrap sm:gap-6 sm:justify-evenly"'>
					{
						getInterest.map((interest, i) => (
							<div key={i}>
								<figure class="px-10 pt-10">
									<img src="https://placeimg.com/400/225/arch" alt="Shoes" class="rounded-xl" />
								</figure>
								<div class="card-body items-center text-center">
									<h2 class="card-title">{interest.interest}</h2>
									<p>{interest.caption}</p>
								</div>
							</div>
							
						))
					}
				</div>
				
			</div>
		</section>
	)
}

export default Interest
