import React, { useEffect, useState } from 'react'

const Awards = () => {
	const [getAward, setAward] = useState([]);
	
	const getAwards = () => {
		try {
			setAward(
				[
					{
						award: 'Pemrograman WEB',
						caption: 'Pemrograman WEB adalah ...',
						imageLink: './img/awards/html-css-js.svg',
					},
					{
						award: 'Desain UI/UX',
						caption: 'Desain UI/UX adalah ...',
						imageLink: './img/awards/react.svg',
					},
					{
						award: 'Desain Grafis',
						caption: 'Desain Grafis adalah ...',
						imageLink: './img/awards/Tailwind_CSS_logo.svg',
					},
					{
						award: 'Penulis Konten',
						caption: 'Penulis Konten adalah ...',
						imageLink: './img/awards/Bootstrap_logo.svg',
					},
				]
			)
		} catch (error) {
			console.log(error.message);
		}
	}

	useEffect(() => {
		getAwards();
	}, [])

	return (
		<section id='awards' className='mt-24'>
			<div className='p-5 text-center'>
				<h1 className="text-5xl font-bold">Awards and Sertifications</h1>
				<p className="py-6">
				Tidak ada usaha yang sia-sia. Sekecil apapun yang kita lakuakn pasti akan ada imbalannya
				</p>
			</div>
			<div className="container mx-auto px-6 font-inter bg-red-500 sm:flex sm:flex-wrap sm:gap-6 sm:justify-evenly">

			</div>
			<div className="card w-96 m-5 bg-base-100 shadow-xl">
				<figure className="px-10 pt-10">
					<img src="https://placeimg.com/400/225/arch" alt="Shoes" className="rounded-xl" />
				</figure>
				<div className="card-body items-center text-center">
					<h2 className="card-title">Shoes!</h2>
					<p>If a dog chews shoes whose shoes does he choose?</p>
					<div className="card-actions">
					<button className="btn btn-primary">Buy Now</button>
					</div>
				</div>
			</div>
		</section>
	)
}

export default Awards
