import React from "react";
import { propTypes } from "react-bootstrap/esm/Image";

class HeroName extends React.Component {
	render(){
		return (
			<>
				<h3>Hero Name</h3>
				<p>{this.props.name}</p>
			</>
		)
	}
}

HeroName.propTypes = {
	name: propTypes.string
}

