class Hero extends React.Component {
	constructor(){
		super()
		this.state = {
			name: 'Gatot Kaca'
		}
	}

	render() {
		return (
			<>
				<p>Step one </p>
				<HeroName name='hercules' />
				<hr></hr>
				<p>Step two using state</p>
				<HeroName name={this.state.name} />
			</>
		)
	}
}

export default Hero;
