import React from 'react';

export default class Users extends React.Component {
	constructor(){
		super()
		this.state = {
			username: 'user001',
			password: '12345',
		}
	}

	getUsername(){
		return this.state.username
	}

	setUsername(){
		this.setState({
			username: 'user01'
		})
	}

	render() {
		return (
			<>
				<h1>Belajar State</h1>
				<hr />
				{/* step one get this.state */}
				<div className='row'>
					<div className='col'>Username : {this.state.username}</div>
					<div className='col'>Password : {this.state.password}</div>
				</div>
				{/* step two return getusername this.state */}
				<div className='row'>
					<div className='col'>Username : {this.state.getUsername()}</div>
					<div className='col'>Password : {this.state.getPass()}</div>
				</div>
				{/* step three set data baru sate */}
				<div className='row'>
					<div className='col'>
						Lihat perubahan set data &gt; Username : {this.getUsername()}
					</div>
					<div className='col'>
						Lihat perubahan set data &gt; Password : {this.getPass()}
					</div>
				</div>
			</>
    );
	}
}
