import React from "react";

class HeroName extends React.Component {
	render(){
		return (
			<>
				<h3>Hero Name</h3>
				<p>{this.props.name}</p>
			</>
		)
	}
}

HeroName.propTypes = {
	name: propTypes.string
}

export default class Hero extends React.Component {
	constructor() {
		super()
		this.state = {
			// name: "Hercules"
			name : 'hercules'
		}
	}

	render() {
		return <HeroName name={this.state.name} />
	}
}
