import './App.css';
import { useEffect, useState } from 'react';
import  axios from 'axios';

function App(props) {
	const [base, setBase] = useState([]);
	const [currencies, setCurrencies] = useState([]);
	const [identifier, setIdentifier] = useState(1);
	const [loading, setLoading] = useState(false);

	const getCurrencies = async () => {
		setLoading(true)
		try {
			let response = await axios.get(`https://api.currencyfreaks.com/latest?apikey=7853f4e1f6094bf492a3213a6ee5e381`)
			setBase(response.data.base);
			setCurrencies(response.data.rates);
			setLoading(false)
		} catch(e) {
			setLoading(true)
			console.log(e.message);
		}
	}

	useEffect(() => {
		getCurrencies();
	}, [identifier])

	return (
		<div className='py-5'>
			<div className='container'>
				<div className='row justify-content-center'>
					<div className='col-md-8'>
						<input type='text' name='identifier' value={identifier} onChange={(e) => setIdentifier(e.target.value)} className='form-control'/> <label>{base}</label>
						{
							loading ? <div>Loading .... </div> :
							<div className='overflow-x-auto'>
								<table className='table table-zebra w-full'>
								<thead>
									<tr>
										<th>Name</th>
										<th>Username</th>
										<th>Email</th>
										<th>Website</th>
										<th>Phone</th>
									</tr>
								</thead>
								<tbody>
									<tr>
										<td>{currencies.CAD}</td>
										<td>{currencies.IDR}</td>
										<td>{currencies.CAD}</td>
										<td>{currencies.CAD}</td>
										<td>{currencies.CAD}</td>
									</tr>
									{/* {
										currencies.map((currency, index) => {
											return (
												<tr key={index}>
													<td>{currency.rates.CAD}</td>
													<td>{currency.username}</td>
													<td>{currency.email}</td>
													<td>{currency.website}</td>
													<td>{currency.phone}</td>
												</tr>
											)
										})
									} */}
								</tbody>
							</table>
							</div>

						}
					</div>
				</div>
			</div>
		</div>
	);
}

export default App;
