import './App.css';
import { useEffect, useState } from 'react';
import  axios from 'axios';

function App(props) {
	const [base, setBase] = useState([]);
	const [currencies, setCurrencies] = useState([]);
	const [identifier, setIdentifier] = useState(1);
	const [loading, setLoading] = useState(false);

	const getCurrency = async () => {
		setLoading(true)
		try {
			let response = await axios.get(`https://api.currencyfreaks.com/latest?apikey=7853f4e1f6094bf492a3213a6ee5e381`)
			setBase(response.data.base);
			setCurrencies([
				{
					name: 'CAD',
					rate: (response.data.rates.CAD),
				},
				{
					name: 'IDR',
					rate: (response.data.rates.IDR),
				},
				{
					name: 'JPY',
					rate: (response.data.rates.JPY),
				},
				{
					name: 'CHF',
					rate: (response.data.rates.CHF),
				},
				{
					name: 'EUR',
					rate: (response.data.rates.EUR),
				},
				{
					name: 'GBP',
					rate: (response.data.rates.GBP),
				}
			])


			setLoading(false)
		} catch(e) {
			setLoading(true)
			console.log(e.message);
		}
	}

	useEffect(() => {
		getCurrency();
	}, [identifier])

	return (
		<div className='py-5 flex'>
			<div className='container mx-auto'>
				{/* <input type='text' name='identifier' value={identifier} onChange={(e) => setIdentifier(e.target.value)} className='form-control '/> <label>{base}</label> */}
				<h1 className='text-4xl text-center'>Display Currency Rates</h1>
				<div className="form-control pb-10">
					<label className="label">
						<span className="label-text">Enter amount</span>
					</label>
					<label className="input-group">
						<input type="text" className="input input-bordered" name='identifier' value={identifier} onChange={(e) => setIdentifier(e.target.value)}/>
						<span>{base}</span>
					</label>
				</div>
				{
					loading ? <div>Loading .... </div> :
					<div className='overflow-x-auto'>
						<table className='table table-zebra w-full'>
							<thead>
								<tr className='text-2xl'>
									<th>Currency</th>
									<th>We Buy</th>
									<th>Exchange Rate</th>
									<th>We Sell</th>
								</tr>
							</thead>
							<tbody>
								{
									currencies.map((currency, index) => {
										let valueRate = parseFloat(currency.rate) * parseFloat(identifier)
										let valueBuy = parseFloat(currency.rate) + 0.05 * (currency.rate) * parseFloat(identifier)
										let valueSell = parseFloat(currency.rate) - 0.05 * (currency.rate) * parseFloat(identifier)
										return (
											<tr key={index}>
												<td>{currency.name}</td>
												<td>{valueBuy}</td>
												<td>{valueRate}</td>
												<td>{valueSell}</td>
											</tr>
										)
									})
								}
							</tbody>
						</table>
					</div>
				}
			</div>
		</div>
	);
}

export default App;
