import React, { Component } from 'react'

export default class Halu extends Component {
	constructor(props){
		super(props);
		this.state = {
			count: 0,
			users: [],
			isLoading: true
		};
	}

	shouldComponentUpdate(nextProps, nextState){ //membatasi, keadaan / kejadian apa saja yang bisa men-trigger re-render, defaultnya true
		if (this.state.users.length !== nextState.users.length){
			return true;
		}
		return false;
	}

	componentDidUpdate(prevProps) {
		if (prevProps.selectedState !== this.props.selectedState) {
			fetch('https://jsonplaceholder.typicode.com/todos')
				.then(resp => resp.json())
				.then(respJson => {
					// do what ever you want with your 'response'
					let postTodo = respJson.map((item) => {
						return (
							<>
								<h1>ID : {item}</h1>
							</>
						)
					})
					this.setState({
						isLoading: false,
						data: postTodo,
					});
				})
				.catch(err => {
					console.log(err)
				})
		}
	}

	// eventData add event listener
	componentWillUnmount(){
		eventData.addEventListener()
	}

	// eventData remove event listener (untuk menghapus)
	componentWillUnmount(){
		eventData.removeEventListener()
	}


	render() {
		return (
			<div>
				<h1>{this.state.users}</h1>
			</div>
		)
	}

}
